import React, { useState, useRef, useEffect } from 'react';

function AutocompleteTextbox() {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const inputRef = useRef(null);
  let timeoutId = null;

  const handleInputChange = (event) => {
    const newValue = event.target.value;
    setInputValue(newValue);

    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      // Call function to query ChatGPT and set suggestions
    }, 3000); // 3000 milliseconds = 3 seconds
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Tab') {
      event.preventDefault(); // Prevent the default Tab behavior

      if (suggestions.length > 0) {
        const selectedSuggestion = suggestions[0]; // For example, pick the first suggestion
        setInputValue(selectedSuggestion);
        setSuggestions([]); // Clear suggestions
      }
    }
  };

  useEffect(() => {
    return () => clearTimeout(timeoutId);
  }, []);

  return (
    <div>
      <input
        ref={inputRef}
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        onKeyDown={handleKeyDown} /* Attach the handleKeyDown function */
      />
      {/* Render autocomplete suggestions */}
      <ul>
        {suggestions.map((suggestion, index) => (
          <li key={index}>{suggestion}</li>
        ))}
      </ul>
    </div>
  );
}

export default AutocompleteTextbox;
